import React, { useState, useEffect } from "react";
import axios from "axios";
import "./styles.css";

const CreateTable = ({ token }) => {
  const [tableName, setTableName] = useState("");
  const [fields, setFields] = useState([
    { name: "", type: "", is_unique: false },
  ]);

  const fieldTypes = ["VARCHAR(255)", "INTEGER", "DATE", "BOOLEAN"];

  const handleAddField = () => {
    setFields([...fields, { name: "", type: "", is_unique: false }]);
  };

  const handleFieldChange = (index, event) => {
    const values = [...fields];
    values[index][event.target.name] = event.target.value;
    setFields(values);
  };

  const handleDeleteField = (index) => {
    const values = [...fields];
    values.splice(index, 1);
    setFields(values);
  };

  const handleUniqueChange = (index) => {
    const values = [...fields];
    values[index].is_unique = !values[index].is_unique;
    setFields(values);
  };

  const handleCreateTable = async () => {
    try {
      const response = await axios.post(
        "http://127.0.0.1:8000/api/create_table/",
        { name: tableName, fields: fields },
        { headers: { Authorization: `Bearer ${token}` } }
      );
      console.log("Table created:", response.data);
    } catch (error) {
      console.error("Error creating table:", error);
    }
  };

  return (
    <div className="create-table-container">
      <h2>Create Table</h2>
      <input
        type="text"
        className="input-table-name"
        value={tableName}
        onChange={(e) => setTableName(e.target.value)}
        placeholder="Table Name"
      />
      <div className="fields-container">
        {fields.map((field, index) => (
          <div key={index} className="field-row">
            <input
              type="text"
              className="input-field-name"
              name="name"
              value={field.name}
              onChange={(e) => handleFieldChange(index, e)}
              placeholder="Field Name"
            />
            <select
              name="type"
              className="select-field-type"
              value={field.type}
              onChange={(e) => handleFieldChange(index, e)}
            >
              <option value="">Select Type</option>
              {fieldTypes.map((type, i) => (
                <option key={i} value={type}>
                  {type}
                </option>
              ))}
            </select>
            <label className="checkbox-label">
              <input
                type="checkbox"
                checked={field.is_unique}
                onChange={() => handleUniqueChange(index)}
              />
              Unique
            </label>
            <button
              className="delete-field-btn"
              onClick={() => handleDeleteField(index)}
            >
              Delete
            </button>
          </div>
        ))}
      </div>
      <button className="add-field-btn" onClick={handleAddField}>
        Add Field
      </button>
      <button className="create-table-btn" onClick={handleCreateTable}>
        Create Table
      </button>
    </div>
  );
};

export default CreateTable;
