import React, { useState } from "react";
import axios from "axios";

const CreateTable = ({ token }) => {
  const [tableName, setTableName] = useState("");
  const [fields, setFields] = useState([
    { name: "", type: "", is_unique: false },
  ]);

  const fieldTypes = ["VARCHAR(255)", "INTEGER", "DATE", "BOOLEAN"]; // Add more field types as needed

  const handleAddField = () => {
    setFields([...fields, { name: "", type: "", is_unique: false }]);
  };

  const handleFieldChange = (index, event) => {
    const values = [...fields];
    values[index][event.target.name] = event.target.value;
    setFields(values);
  };

  const handleDeleteField = (index) => {
    const values = [...fields];
    values.splice(index, 1);
    setFields(values);
  };

  const handleUniqueChange = (index) => {
    const values = [...fields];
    values[index].is_unique = !values[index].is_unique;
    setFields(values);
  };

  const handleCreateTable = async () => {
    console.log("Table Name:", tableName);
    console.log("Fields:", fields); // Check the fields data

    try {
      const response = await axios.post(
        "http://127.0.0.1:8000/api/create_table/",
        {
          name: tableName,
          fields: fields,
        },
        {
          headers: {
            Authorization: `Bearer ${token}`,
          },
        }
      );
      console.log("Table created:", response.data);
    } catch (error) {
      console.error("Error creating table:", error);
    }
  };

  const handleDeleteTable = async () => {
    try {
      const response = await axios.delete(
        `http://127.0.0.1:8000/api/delete_table/${tableName}/`,
        {
          headers: {
            Authorization: `Bearer ${token}`,
          },
        }
      );
      console.log("Table deleted:", response.data);
    } catch (error) {
      console.error("Error deleting table:", error);
    }
  };

  return (
    <div>
      <h2>Create Table</h2>
      <input
        type="text"
        value={tableName}
        onChange={(e) => setTableName(e.target.value)}
        placeholder="Table Name"
      />
      <div>
        {fields.map((field, index) => (
          <div key={index}>
            <input
              type="text"
              name="name"
              value={field.name}
              onChange={(e) => handleFieldChange(index, e)}
              placeholder="Field Name"
            />
            <select
              name="type"
              value={field.type}
              onChange={(e) => handleFieldChange(index, e)}
            >
              <option value="">Select Type</option>
              {fieldTypes.map((type, i) => (
                <option key={i} value={type}>
                  {type}
                </option>
              ))}
            </select>
            <label>
              <input
                type="checkbox"
                checked={field.is_unique}
                onChange={() => handleUniqueChange(index)}
              />
              Unique
            </label>
            <button onClick={() => handleDeleteField(index)}>
              Delete Field
            </button>
          </div>
        ))}
      </div>
      <button onClick={handleAddField}>Add Field</button>
      <button onClick={handleCreateTable}>Create Table</button>
      <button onClick={handleDeleteTable}>Delete Table</button>
    </div>
  );
};

export default CreateTable;
