import React, { useState, useEffect } from "react";
import axios from "axios";

const CreateTable = ({ token }) => {
  const [tableName, setTableName] = useState("");
  const [fields, setFields] = useState([
    { name: "", type: "", is_unique: false },
  ]);
  const [searchQuery, setSearchQuery] = useState("");
  const [tables, setTables] = useState([]);
  const [showAll, setShowAll] = useState(false); // State to manage "Show All"
  const [selectedTable, setSelectedTable] = useState(null); // Store selected table for field display

  const fieldTypes = ["VARCHAR(255)", "INTEGER", "DATE", "BOOLEAN"];

  useEffect(() => {
    fetchTables();
  }, []);

  const fetchTables = async () => {
    try {
      const response = await axios.get(
        "http://127.0.0.1:8000/api/list_tables/",
        {
          headers: { Authorization: `Bearer ${token}` },
        }
      );
      setTables(response.data.tables);
    } catch (error) {
      console.error("Error fetching tables:", error);
    }
  };

  const handleSearchChange = (event) => {
    setSearchQuery(event.target.value);
    setSelectedTable(null); // Reset selected table when searching
  };

  const filteredTables = tables.filter((table) =>
    table.toLowerCase().includes(searchQuery.toLowerCase())
  );

  const handleTableClick = async (tableName) => {
    try {
      const response = await axios.get(
        `http://127.0.0.1:8000/api/get_table_fields/${tableName}/`,
        {
          headers: { Authorization: `Bearer ${token}` },
        }
      );
      setSelectedTable(response.data.fields); // Set selected table fields to display
    } catch (error) {
      console.error("Error fetching table fields:", error);
    }
  };

  const handleAddField = () => {
    setFields([...fields, { name: "", type: "", is_unique: false }]);
  };

  const handleFieldChange = (index, event) => {
    const values = [...fields];
    values[index][event.target.name] = event.target.value;
    setFields(values);
  };

  const handleDeleteField = (index) => {
    const values = [...fields];
    values.splice(index, 1);
    setFields(values);
  };

  const handleUniqueChange = (index) => {
    const values = [...fields];
    values[index].is_unique = !values[index].is_unique;
    setFields(values);
  };

  const handleCreateTable = async () => {
    try {
      await axios.post(
        "http://127.0.0.1:8000/api/create_table/",
        { name: tableName, fields },
        { headers: { Authorization: `Bearer ${token}` } }
      );
      fetchTables(); // Refresh the table list
    } catch (error) {
      console.error("Error creating table:", error);
    }
  };

  const handleDeleteTable = async () => {
    try {
      await axios.delete(
        `http://127.0.0.1:8000/api/delete_table/${tableName}/`,
        {
          headers: { Authorization: `Bearer ${token}` },
        }
      );
      fetchTables(); // Refresh the table list
    } catch (error) {
      console.error("Error deleting table:", error);
    }
  };

  return (
    <div className="table-container">
      <h2 className="table-header">Create Table</h2>
      <input
        type="text"
        value={tableName}
        onChange={(e) => setTableName(e.target.value)}
        placeholder="Table Name"
        className="table-input"
      />

      <div className="fields-container">
        {fields.map((field, index) => (
          <div key={index} className="field-row">
            <input
              type="text"
              name="name"
              value={field.name}
              onChange={(e) => handleFieldChange(index, e)}
              placeholder="Field Name"
              className="field-input"
            />
            <select
              name="type"
              value={field.type}
              onChange={(e) => handleFieldChange(index, e)}
              className="field-select"
            >
              <option value="">Select Type</option>
              {fieldTypes.map((type, i) => (
                <option key={i} value={type}>
                  {type}
                </option>
              ))}
            </select>
            <label className="checkbox-label">
              <input
                type="checkbox"
                checked={field.is_unique}
                onChange={() => handleUniqueChange(index)}
              />
              Unique
            </label>
            <button
              onClick={() => handleDeleteField(index)}
              className="delete-field-btn"
            >
              Delete Field
            </button>
          </div>
        ))}
      </div>

      <button onClick={handleAddField} className="add-field-btn">
        Add Field
      </button>
      <button onClick={handleCreateTable} className="create-table-btn">
        Create Table
      </button>
      <button onClick={handleDeleteTable} className="delete-table-btn">
        Delete Table
      </button>

      {/* Search Tables */}
      <h2 className="table-header">Search Tables</h2>
      <input
        type="text"
        value={searchQuery}
        onChange={handleSearchChange}
        placeholder="Search Tables"
        className="field-input"
      />

      <button onClick={() => setShowAll(!showAll)} className="show-all-btn">
        {showAll ? "Show Less" : "Show All"}
      </button>

      <ul className="table-list">
        {showAll ? (
          tables.map((table, index) => (
            <li
              key={index}
              className="table-item"
              onClick={() => handleTableClick(table)}
            >
              {table}
            </li>
          ))
        ) : searchQuery && filteredTables.length === 1 ? (
          filteredTables.map((table, index) => (
            <li
              key={index}
              className="table-item"
              onClick={() => handleTableClick(table)}
            >
              {table}
            </li>
          ))
        ) : searchQuery ? (
          <li className="table-item">Table doesn't exist</li>
        ) : null}
      </ul>

      {/* Display table fields if selected */}
      {selectedTable && (
        <div className="table-fields">
          <h3>Table Fields:</h3>
          <ul>
            {selectedTable.map((field, index) => (
              <li key={index}>
                {field.name} ({field.type})
              </li>
            ))}
          </ul>
        </div>
      )}
    </div>
  );
};

export default CreateTable;
