import React, { useState } from "react";
import axios from "axios";
import CreateTable from "./components/CreateTable";
import CRUDOperations from "./components/CRUDOperations";
import ImportCSV from "./components/ImportCSV";

const App = () => {
  const [token, setToken] = useState("");
  const [username, setUsername] = useState("");
  const [password, setPassword] = useState("");
  const [email, setEmail] = useState("");

  const handleLogin = async () => {
    try {
      const response = await axios.post("http://127.0.0.1:8000/api/token/", {
        username,
        password,
        email,
      });

      setToken(response.data.access);
      localStorage.setItem("userEmail", email);
    } catch (error) {
      console.error("Login failed", error);
    }
  };

  return (
    <div className="min-h-screen flex items-center justify-center bg-gradient-to-r from-purple-600 to-blue-600 p-6">
      <div className="w-full max-w-lg bg-white bg-opacity-10 backdrop-blur-md shadow-2xl rounded-2xl p-8 text-white">
        <h1 className="text-3xl font-extrabold text-center mb-6 drop-shadow-lg">
          Data Management
        </h1>

        {!token ? (
          <div className="space-y-4">
            <input
              type="text"
              value={username}
              onChange={(e) => setUsername(e.target.value)}
              placeholder="Username"
              className="w-full p-3 text-lg bg-white bg-opacity-20 placeholder-white focus:ring-2 focus:ring-blue-300 rounded-lg outline-none"
            />
            <input
              type="password"
              value={password}
              onChange={(e) => setPassword(e.target.value)}
              placeholder="Password"
              className="w-full p-3 text-lg bg-white bg-opacity-20 placeholder-white focus:ring-2 focus:ring-blue-300 rounded-lg outline-none"
            />
            <input
              type="email"
              value={email}
              onChange={(e) => setEmail(e.target.value)}
              placeholder="Email"
              className="w-full p-3 text-lg bg-white bg-opacity-20 placeholder-white focus:ring-2 focus:ring-blue-300 rounded-lg outline-none"
            />
            <button
              onClick={handleLogin}
              className="w-full py-3 text-lg font-semibold bg-gradient-to-r from-pink-500 to-purple-500 rounded-lg shadow-md transform transition hover:scale-105 hover:shadow-2xl"
            >
              üîê Login
            </button>
          </div>
        ) : (
          <div className="space-y-4">
            <CreateTable token={token} />
            <CRUDOperations token={token} />
            <ImportCSV token={token} email={email} />
          </div>
        )}
      </div>
    </div>
  );
};

export default App;
