import React, { useState, useEffect } from "react";
import axios from "axios";

const CreateTable = ({ token }) => {
  const [tableName, setTableName] = useState("");
  const [fields, setFields] = useState([
    { name: "", type: "", is_unique: false },
  ]);
  const [searchQuery, setSearchQuery] = useState("");
  const [tables, setTables] = useState([]);
  const [filteredTables, setFilteredTables] = useState([]);

  const fieldTypes = ["VARCHAR(255)", "INTEGER", "DATE", "BOOLEAN"];

  useEffect(() => {
    fetchTables();
  }, []);

  const fetchTables = async () => {
    try {
      const response = await axios.get(
        "http://127.0.0.1:8000/api/list_tables/",
        {
          headers: { Authorization: `Bearer ${token}` },
        }
      );
      setTables(response.data.tables);
      setFilteredTables(response.data.tables); // Initialize filtered list
    } catch (error) {
      console.error("Error fetching tables:", error);
    }
  };

  const handleSearch = () => {
    setFilteredTables(
      tables.filter((table) =>
        table.toLowerCase().includes(searchQuery.toLowerCase())
      )
    );
  };

  return (
    <div className="table-container">
      <h2 className="table-header">Create Table</h2>
      <input
        type="text"
        value={tableName}
        onChange={(e) => setTableName(e.target.value)}
        placeholder="Table Name"
        className="table-input"
      />

      {/* Search Tables */}
      <h2 className="table-header">Search Tables</h2>
      <div className="search-container">
        <input
          type="text"
          value={searchQuery}
          onChange={(e) => setSearchQuery(e.target.value)}
          placeholder="Search Tables"
          className="search-input"
        />
        <button onClick={handleSearch} className="search-btn">
          Search
        </button>
      </div>
      <ul className="table-list">
        {filteredTables.map((table, index) => (
          <li key={index} className="table-item">
            {table}
          </li>
        ))}
      </ul>
    </div>
  );
};

export default CreateTable;
